import { useState } from 'react';
import './App.css';

function App() {
  const state = useState();
  const [count, setCount] = useState(0);
  const Increment = () => {
    setCount(count + 1);
  }
  const decrement = () => {
    setCount(count-1);
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Counter App</h1>
        <p>{ count}</p>
        <button onClick={Increment}>Add</button>
        <button onClick={decrement}>Decrement</button>
      </header>
    </div>
  );
}

export default App;
